import React, { useState, useEffect } from 'react';
import {theJoke} from '../../utils/API'

const JokeContainer = () => {
  const [result, setResult] = useState({});

  var aboutStyle = {
    fontSize: "1.5rem",
    color: 'black',
    display: "block",
    left: "2%",
    paddingLeft: '2%',
    width: '60%',
    paddingBottom: '2%',
    textIndent: '50px'
  };

  var picStyle = {

    display: 'block',
    paddingBottom: '3%',
    paddingTop: '1%',
    paddingLeft: '2%'
  }

  var textStyle = {
    paddingLeft: '2%',
    fontSize: "2rem"
  }

  const css = `@media (min-width: 320px) {
    img {
      width: 70%;
    }
  }
  @media (min-width: 768px) {
    img {
      width: 50%;
    }
  }
  @media (min-width: 1200px) {
    img {
      width: 25%;
  }
  }`;

  useEffect(() => {
    theJoke().then((res) => {
      console.log("res.data.joke", res.data.joke);
      console.log("res.data.setup", res.data.setup);
      console.log("res.data.delivery", res.data.delivery);
      setResult(res.data);
    });
  }, []);

  return (
    <div>
      <label>Joke Category:</label>
        <select id="categoryList">
            <option>Choose </option>
            <option>Any</option>
            <option>Misc</option>
            <option>Programming</option>
            <option>Pun</option>
            <option>Halloween</option>
            <option>Christmas</option>
            </select>
            <br/>
            <button id="joke-btn">New Joke!</button>
            <br />
      {result.joke}
      {result.setup}
      {result.delivery}
      <br />
      <button>Save Joke!</button>
      <br />
      {/* <button>New Joke!</button> */}
    </div>
  );
};

export default JokeContainer
